import React from 'react';
import { Body, DeleteButton, GlobalStyle, HeaderButtonDiv, InputLogin, SendContainer, TableDiv } from './styled';
import axios from 'axios';
import useRequestData from '../../hooks/useRequestData';
import DeleteIcon from '@mui/icons-material/Delete';
import { BASE_URL } from '../../constants/urls';
import PizzaCard from '../../components/PizzaCard/PizzaCard';
import SwipeableEdgeDrawer from '../../components/testBorda/testeBorda';


function IndexPage() {

  const [pizzas, request] = useRequestData(`${BASE_URL}/api/pizzas`, [])

  // console.log(pizzas);
  const pizzasMap = pizzas && pizzas.map((pizza)=>{
    return (
      <div key={pizza.id}>
        <PizzaCard  pizza={pizza}/>
      </div>
      )
  })


  
  const deletePizzas =  async () =>{
    if (window.confirm("Tem certeza que deseja excluir os usu치rios?")) {
   const url = "https://case1-cubonetworking.herokuapp.com/user";
   await axios.delete(url)
    .then((response) =>{
      // alert("Usu치rios deletados com sucesso.")
      // clearForm()
      request()
    })
    .catch((error) =>{
      alert("Erro!")
      console.log(error)
    })
  }
}

  

  return (
    <Body>
      <GlobalStyle />
      <header>
        <HeaderButtonDiv>
        
        </HeaderButtonDiv>
      </header>
      <main>
      <div>
          {pizzas ? (pizzasMap): <h1>Restaurante fechado</h1>}
        </div>
        <div>
        <DeleteButton onClick={deletePizzas}><DeleteIcon/></DeleteButton>
        </div>
      </main>
      <SwipeableEdgeDrawer/>
    </Body>
  )
}

export default IndexPage
