import { Typography } from '@mui/material';
import React from 'react'
import CartCard from '../../components/CartCard/CartCard';
import Footer from '../../components/Footer/Footer';
import InputText from '../../components/InputText/InputText';
import { useGlobal } from '../../context/global/GlobalStateContext';
import { Body, GlobalStyle } from '../Index/styled';
import { DivHeader } from './styled';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';

function Cart() {
  const { states, setters } = useGlobal()
  const { setOrderMessage, setCart, setLastOrderInfo} = setters
  const { cart, orderMessage, orderInfo, lastOrderInfo} = states
  // console.log(cart);
  return (
    <Body>
      <GlobalStyle />
      <main>
        <DivHeader>
          {cart.length > 0 ? (<CartCard setLastOrderInfo={setLastOrderInfo} lastOrderInfo={lastOrderInfo} setCart={setCart} orderMessage={orderMessage} setOrderMessage={setOrderMessage} cart={cart} />)
            :
            <>
              <InputText />
            </>
          }
          {orderInfo? <Card sx={{ minWidth: 275 }}>
                <CardContent>
                  <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                    Informações do pedido
                  </Typography>
                  <Typography variant="h5" component="div">
                    Pizza de {orderInfo.pizza}
                  </Typography>
                  <Typography sx={{ mb: 1.5 }} color="text.secondary">
                    ID: {orderInfo.id}
                  </Typography>
                  <Typography variant="body2">
                    Quantidade: {orderInfo.quantity}
                  </Typography>
                </CardContent>
              </Card>
              :
              <>
              </>}

        </DivHeader>
      </main>
      <Footer />
    </Body>
  )
}

export default Cart
