import React from 'react';
import { Body, GlobalStyle, HeaderButtonDiv } from './styled';
import useRequestData from '../../hooks/useRequestData'
import { BASE_URL } from '../../constants/urls';
import PizzaCard from '../../components/PizzaCard/PizzaCard';
import SwipeableEdgeDrawer from '../../components/testBorda/testeBorda';
import { Button } from '@mui/material';


function IndexPage() {

  const [pizzas] = useRequestData(`${BASE_URL}/api/pizzas`, [])


  const pizzasMap = pizzas && pizzas.map((pizza)=>{
    return (
      <div key={pizza.id}>
        <PizzaCard  pizza={pizza}/>
      </div>
      )
  })


  

  return (
    <Body>
      <GlobalStyle />
      <header>
        <HeaderButtonDiv>
        <Button>Admin</Button>
        </HeaderButtonDiv>
      </header>
      <main>
      <div>
          {pizzas ? (pizzasMap): <h1>Restaurante fechado</h1>}
        </div>
      </main>
      <SwipeableEdgeDrawer/>
    </Body>
  )
}

export default IndexPage
